import { numberValidator } from '../validation';
export default (random, min = 0, max = 1) => {
    if (max === undefined) {
        max = min === undefined ? 1 : min;
        min = 0;
    }
    numberValidator(min).isInt();
    numberValidator(max).isInt();
    return () => {
        return Math.floor(random.next() * (max - min + 1) + min);
    };
};
