import React from 'react'
import {useHistory} from 'react-router-dom'
import ferDuclos from '../../images/ferDuclos.jpeg'
import {Button, Image} from 'react-bootstrap'
import random from 'random'
import './styleLanding.css'


export default function LandingPage(){

const history = useHistory()
const randomId = random.int((0), (10))

const handleClick = () =>{
    switch (random.int((0), (4))) {
        case 0:
        history.push(`/amerika/${randomId}`)
          break;
        case 1:
            history.push(`/afrika/${randomId}`)
          break;
        case 2:
            history.push(`/asi/${randomId}`)
          break;
        case 3:
            history.push(`/europ/${randomId}`)
          break;
          default:
            history.push(`/afrika/${randomId}`)
      }
}

    return (
        <div className='landing-container'>
            <div className='landing-text'>
                <h1 >Bienvenido a Periodistán </h1> 
                <h5 >Aquí vas a poder encontrar la mayoría de los hilos de Fernando Duclos (alias Periodistán)</h5>
                <Button
                size="lg"  
                style={{background:"#1e212d", border: "none"}} 
                onClick={handleClick}>
                    Hilo Random
                </Button>
            </div>
            <div className='landing-image'>
                <Image className='main-image' src={ferDuclos} alt='ferDuclos'/> 
            </div>
        </div>
    )
}
